import React, { useState, useEffect } from 'react';
import axios from 'axios';

// 환경 변수 타입 선언
declare const process: {
  env: {
    NODE_ENV: string;
  };
};

interface TACOSignal {
  id: number;
  signal_type: string;
  confidence: number;
  statement: {
    original: string;
    korean: string;
    keywords: string[];
    posted_at: string;
  };
  affected_etfs: any[];
  created_at: string;
}

interface TrumpStatement {
  id: number;
  original_text: string;
  korean_translation: string;
  keywords: string[];
  taco_probability: number;
  time_ago: string;
}

interface ETFPrice {
  symbol: string;
  price: number;
  change_percent: number;
  volume: number;
}

interface PerformanceData {
  total_return_30d: number;
  win_rate: number;
  total_profit: number;
  total_signals: number;
  successful_signals: number;
  average_holding_days: number;
}

function App() {
  const [signals, setSignals] = useState<TACOSignal[]>([]);
  const [statements, setStatements] = useState<TrumpStatement[]>([]);
  const [etfPrices, setETFPrices] = useState<ETFPrice[]>([]);
  const [performance, setPerformance] = useState<PerformanceData | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdate, setLastUpdate] = useState<Date>(new Date());

  useEffect(() => {
    fetchData();
    
    // 30초마다 데이터 새로고침
    const interval = setInterval(() => {
      console.log('🔄 자동 데이터 새로고침...');
      fetchData();
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  const fetchData = async () => {
    try {
      setError(null);
      // 개발 환경에서는 백엔드 서버 직접 호출
      const baseURL = 'http://localhost:8000';
      
      console.log('🌮 백엔드 서버 연결 시도...');
      
      const [signalsRes, statementsRes, pricesRes, performanceRes] = await Promise.all([
        axios.get(`${baseURL}/api/latest-signals`),
        axios.get(`${baseURL}/api/trump-feed`),
        axios.get(`${baseURL}/api/etf-prices`),
        axios.get(`${baseURL}/api/performance`)
      ]);

      console.log('✅ 데이터 로드 성공!');
      console.log('신호 수:', signalsRes.data.signals?.length || 0);
      console.log('발언 수:', statementsRes.data.statements?.length || 0);
      console.log('ETF 수:', pricesRes.data.etf_prices?.length || 0);

      setSignals(signalsRes.data.signals || []);
      setStatements(statementsRes.data.statements || []);
      setETFPrices(pricesRes.data.etf_prices || []);
      setPerformance(performanceRes.data);
      setLastUpdate(new Date());
      setLoading(false);
    } catch (error: any) {
      console.error('❌ 데이터 로드 오류:', error);
      console.error('에러 상세:', error.response?.data || error.message);
      
      const errorMessage = error.response?.status === 404 
        ? '백엔드 API를 찾을 수 없습니다. 서버가 실행 중인지 확인하세요.'
        : error.code === 'ECONNREFUSED' 
        ? '백엔드 서버 연결 실패. localhost:8000에서 서버를 실행하세요.'
        : '데이터를 불러오는 중 오류가 발생했습니다.';
      
      setError(errorMessage);
      setLoading(false);
      
      // 오류 발생 시 빈 배열로 설정
      setSignals([]);
      setStatements([]);
      setETFPrices([]);
    }
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#0A0A0A', color: '#fff' }}>
        <div style={{ textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', marginBottom: '10px' }}>🌮</div>
          <div>TACO 데이터 로딩 중...</div>
          <div style={{ fontSize: '0.8rem', color: '#98989D', marginTop: '10px' }}>
            백엔드 서버 연결 중...
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', backgroundColor: '#0A0A0A', color: '#fff' }}>
        <div style={{ textAlign: 'center', maxWidth: '500px', padding: '20px' }}>
          <div style={{ fontSize: '2rem', marginBottom: '20px' }}>❌</div>
          <div style={{ fontSize: '1.2rem', marginBottom: '15px', color: '#FF3B30' }}>
            연결 오류
          </div>
          <div style={{ marginBottom: '20px', color: '#98989D' }}>
            {error}
          </div>
          <div style={{ fontSize: '0.9rem', color: '#636366', lineHeight: '1.5' }}>
            <div>해결 방법:</div>
            <div>1. 백엔드 서버 실행: <code style={{ background: '#1C1C1E', padding: '2px 6px', borderRadius: '4px' }}>cd backend && python -m app.main</code></div>
            <div>2. 서버 주소 확인: <code style={{ background: '#1C1C1E', padding: '2px 6px', borderRadius: '4px' }}>http://localhost:8000</code></div>
          </div>
          <button 
            onClick={fetchData}
            style={{
              marginTop: '20px',
              padding: '10px 20px',
              background: '#007AFF',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              cursor: 'pointer'
            }}
          >
            다시 시도
          </button>
        </div>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px', backgroundColor: '#0A0A0A', color: '#fff', minHeight: '100vh' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '30px' }}>
        🌮 TACO(Trump Always Chickens Out) Trading
      </h1>

      {/* 성과 요약 */}
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '20px', marginBottom: '30px' }}>
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#00D084' }}>
            {performance ? `+${performance.total_return_30d.toFixed(1)}%` : '+127%'}
          </div>
          <div style={{ color: '#98989D' }}>30일 TACO 수익률</div>
        </div>
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#00D084' }}>
            {performance ? `${performance.win_rate.toFixed(0)}%` : '78%'}
          </div>
          <div style={{ color: '#98989D' }}>TACO 성공률</div>
        </div>
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#00D084' }}>
            {performance ? `+$${performance.total_profit.toLocaleString()}` : '+$12,450'}
          </div>
          <div style={{ color: '#98989D' }}>총 TACO 수익</div>
        </div>
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px', textAlign: 'center' }}>
          <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#FF9500' }}>
            {performance ? performance.total_signals : signals.length}
          </div>
          <div style={{ color: '#98989D' }}>실행된 TACO 신호</div>
        </div>
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '20px' }}>
        {/* TACO 신호 */}
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px' }}>
          <h3>🎯 Live TACO Signals</h3>
          {signals.map(signal => (
            <div key={signal.id} style={{
              background: '#2C2C2E',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              borderLeft: `4px solid ${signal.signal_type === 'BUY' ? '#00D084' : signal.signal_type === 'SELL' ? '#FF3B30' : '#FF9500'}`
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
                <span style={{ 
                  color: signal.signal_type === 'BUY' ? '#00D084' : signal.signal_type === 'SELL' ? '#FF3B30' : '#FF9500',
                  fontWeight: 'bold'
                }}>
                  {signal.signal_type === 'BUY' ? '🟢 BUY' : signal.signal_type === 'SELL' ? '🔴 SELL' : '🟡 WATCH'}
                </span>
                <span style={{ color: '#98989D', fontSize: '0.8rem' }}>
                  {new Date(signal.created_at).toLocaleDateString('ko-KR', { 
                    month: 'short', 
                    day: 'numeric', 
                    hour: '2-digit', 
                    minute: '2-digit' 
                  })}
                </span>
              </div>
              <div style={{ fontSize: '1.2rem', fontWeight: 'bold', marginBottom: '10px' }}>
                {Array.isArray(signal.affected_etfs) && signal.affected_etfs.length > 0 
                  ? signal.affected_etfs[0].symbol || 'ETF' 
                  : 'ETF'}
              </div>
              <div style={{ marginBottom: '10px' }}>
                <div style={{ background: '#0A0A0A', height: '6px', borderRadius: '3px' }}>
                  <div style={{
                    background: 'linear-gradient(90deg, #00D084, #FFB81C)',
                    height: '100%',
                    width: `${signal.confidence}%`,
                    borderRadius: '3px'
                  }}></div>
                </div>
                <div style={{ fontSize: '0.9rem', marginTop: '5px' }}>확률: {signal.confidence}%</div>
              </div>
              <div style={{ color: '#98989D', fontSize: '0.9rem', fontStyle: 'italic' }}>
                🌮 "{signal.statement.korean.substring(0, 30)}..."
              </div>
            </div>
          ))}
        </div>

        {/* 트럼프 피드 */}
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px' }}>
          <h3>🎙️ Trump Intelligence Feed</h3>
          {statements.map(stmt => (
            <div key={stmt.id} style={{
              background: '#0A0A0A',
              padding: '15px',
              margin: '10px 0',
              borderRadius: '8px',
              border: '1px solid #38383A'
            }}>
              <div style={{ marginBottom: '10px' }}>{stmt.korean_translation}</div>
              <div style={{ 
                background: 'rgba(255, 255, 255, 0.05)',
                padding: '10px',
                borderRadius: '6px',
                fontSize: '0.8rem'
              }}>
                <div>🔍 키워드: {stmt.keywords.join(', ')}</div>
                <div>🌮 TACO 확률: {stmt.taco_probability}%</div>
              </div>
              <div style={{ 
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
                marginTop: '10px',
                fontSize: '0.8rem',
                color: '#636366'
              }}>
                <span>{stmt.time_ago} • Truth Social</span>
                <div>
                  <span style={{ 
                    background: '#007AFF',
                    color: 'white',
                    padding: '2px 6px',
                    borderRadius: '6px',
                    marginRight: '5px'
                  }}>🤖 AI 번역</span>
                  <span style={{ 
                    background: '#FF3B30',
                    color: 'white',
                    padding: '2px 6px',
                    borderRadius: '6px'
                  }}>높은 영향</span>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* ETF 가격 */}
        <div style={{ background: '#1C1C1E', padding: '20px', borderRadius: '12px' }}>
          <h3>📊 Market Pulse</h3>
          {etfPrices.map(etf => (
            <div key={etf.symbol} style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: '10px',
              margin: '5px 0',
              background: '#0A0A0A',
              borderRadius: '6px'
            }}>
              <span style={{ fontWeight: 'bold', fontFamily: 'monospace' }}>{etf.symbol}</span>
              <span style={{ 
                color: etf.change_percent >= 0 ? '#00D084' : '#FF3B30',
                fontWeight: 'bold',
                fontFamily: 'monospace'
              }}>
                {etf.change_percent >= 0 ? '+' : ''}{etf.change_percent.toFixed(1)}%
              </span>
            </div>
          ))}
        </div>
      </div>

      <div style={{ textAlign: 'center', marginTop: '30px', color: '#636366' }}>
        <div style={{ marginBottom: '10px' }}>
          🌮 TACO Trading - 
          <span style={{ color: '#00D084', marginLeft: '5px' }}>
            ✅ API 연결 성공!
          </span>
        </div>
        <div style={{ fontSize: '0.8rem' }}>
          신호: {signals.length}개 | 발언: {statements.length}개 | ETF: {etfPrices.length}개
        </div>
        <div style={{ fontSize: '0.7rem', marginTop: '5px' }}>
          마지막 업데이트: {lastUpdate.toLocaleTimeString('ko-KR')}
          <span style={{ color: '#00D084', marginLeft: '10px' }}>🔄 30초마다 자동 새로고침</span>
        </div>
      </div>
    </div>
  );
}

export default App;
